import React, {useState} from "react";
import toggleIcon from "../assests/images/icon-hamburger.svg"
import "./styles/toggle.component.css"

const Toggle = () => {

  const [toggle, setToggle] =  useState(false)

  const handleClick = () => {
    setToggle(!toggle)
    console.log(toggle);
  }

  if(toggle === true){
    return(
      <div>
        <div>
          <img onClick={handleClick} src={toggleIcon} alt="toggle icon"/>
        </div>

        <div className="bg-white toggleDiv shadow-2xl rounded-sm">
          <a href></a>
        </div>
      </div>
    )
  }

  return(
    <div>
      <img onClick={handleClick} src={toggleIcon} alt="toggle icon"/>
    </div>
  )
}
export default Toggle
