  const handleClick = () => {
    setToggle(!toggle)
    console.log(toggle);
  }

  if(toggle === true){
    return(
      <div>
        <img onClick={handleClick} src={toggleIcon} alt="toggle icon"/>
        <div className="bg-white w- h-10">

        </div>
      </div>
    )
  }

  return(
    <div>
      <img onClick={handleClick} src={toggleIcon} alt="toggle icon"/>
    </div>
  )
}
export default Toggle
